import React from "react";
import profileImg from "../../assets/profileImg.JPG"
require("./bio.css");

class bio extends React.Component  {
    constructor(props) {
        super(props);
        this.state = {
            infoArr: [
                {
                    id: 0,
                    title: "Title One",
                    content: "Content One"
                },
                {
                    id: 1,
                    title: "Title Two",
                    content: "Content Two"
                },
                {
                    id: 2,
                    title: "Title Three",
                    content: "Content Three"
                }
            ],
            switchValue: 0
        }
        this.handleClickLeft = this.handleClickLeft.bind(this);
        this.handleClickRight = this.handleClickRight.bind(this);

    }

    // increase = () => {
    //     this.setState = {
    //         switchValue: this.state.switchValue + 1
    //     }
    //     console.log(this.state.switchValue)
    // }

    // decrease = () => {
    //     this.setState = {
    //         switchValue: this.state.switchValue - 1
    //     }
    //     console.log(this.state.switchValue)

    // }

    handleClickLeft() {
        console.log("Left")
        console.log(this.switchValue)
        this.setState = {
            switchValue: -1
        };
        console.log(this.switchValue)
    }

    handleClickRight() {
        console.log("Right")
        console.log(this.switchValue)
        this.setState = {
            switchValue: +1
        };
        console.log(this.switchValue)
    }







    render() {
        return (
            <div id="bioMainContainer">
                <div className="row">
                    <div className="col-sm-12 col-md-12 col-lg-5 d-flex  flex-column">
                    <div className="d-flex justify-content-center">
                        <img id="bioProfileImg" className="rounded p-2 "  alt="Profile" src={profileImg} />
                    </div>
                        <p id="bioTitle" className="p-2">Valerie Belton</p>
                    </div>
                    <div className="col-sm-12 col-md-12 col-lg-7">

                        <p id="bioTitle">{this.state.infoArr[this.state.switchValue].title}</p>
                        <p id="bioText">
                            {this.state.infoArr[this.state.switchValue].content}
                        </p>
                        <div className="bioContactBtnContainer">
                            <button className="btn btn-primary rounded bioLearnMoreBtn" data-toggle="modal" data-target=".largeView">Learn More</button>
                            <button className="btn btn-primary rounded bioContactBtn">Contact</button>
                            <div class="modal fade largeView" tabindex="-1" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden="true">
                                <div class="modal-dialog modal-lg">
                                    <div className="modal-content">
                                        <img id="AwardTemp" alt="award" src="https://blogs.saphana.com/wp-content/uploads/2015/12/trophy.jpg" />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="d-flex justify-content-center">
                            <div className="row w-100">
                                <button
                                className="col-sm-6 col-md-6 col-lg-6 d-flex justify-content-start arrowCol"
                                onClick={this.handleClickLeft}
                                // onClick={this.state.increase()}
                                >
                                <i className="fas fa-arrow-circle-left arrowFont" />
                                </button>
                                <button
                                className="col-sm-6 col-md-6 col-lg-6 d-flex justify-content-end arrowCol"
                                onClick={this.handleClickRight}
                                // onClick={this.state.decrease()}
                                >
                                <i className="fas fa-arrow-circle-right arrowFont" />
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}

export default bio;
