import React from "react";
import profileImg from "../../assets/profileImg.JPG"
import axios from "axios";

require("./contact.css");


class contact extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

        }
    }

    handleSubmit(e){
        e.preventDefault();
        const name = document.getElementById('name').value;
        const email = document.getElementById('email').value;
        const message = document.getElementById('message').value;
        axios({
            method: "POST", 
            url:"http://localhost:3002/send", 
            data: {
                name: name,   
                email: email,  
                message: message
            }
        }).then((response)=>{
            if (response.data.msg === 'success'){
                alert("Message Sent."); 
                this.resetForm()
            }else if(response.data.msg === 'fail'){
                alert("Message failed to send.");

            }
        })
    }

    resetForm(){
        document.getElementById('formMainContainer').reset();
    }


    render() {
        return (
            <div id="bioMainContainer">
            <div className="row">
                <div className="col-sm-12 col-md-12 col-lg-6 d-flex justify-content-center flex-column">
                <i id="formHome" className="fas fa-home p-2 text-center"></i>
                    <form id="formMainContainer" onSubmit={this.handleSubmit.bind(this)} method="POST">
                        <div className="form-row">
                            <div className="form-group col-md-6">
                            <label htmlFor="inputEmail4">Email</label>
                            <input type="email" className="form-control" id="inputEmail4" placeholder="Email" />
                            </div>
                            <div className="form-group col-md-6">
                            <label htmlFor="inputPassword4">Phone Number</label>
                            <input type="phone" className="form-control" id="inputPassword4" placeholder="Number" />
                            </div>
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputNumber">Number</label>
                            <input type="text" className="form-control" id="inputNumber" placeholder="Number" />
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-6">
                                <label htmlFor="inputCity">Message</label>
                                <input type="text" className="form-control" id="inputQuestion" placeholder="Any questions for us?"/>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                </div>
                <div className="col-sm-12 col-md-12 col-lg-6">
                    <iframe src="https://snazzymaps.com/embed/27427" width="100%" height="600px"></iframe>
                </div>
            </div>
        </div>
        )
    }


}




export default contact;
